const { analyzeMetadata } = require(''./dist/lib/analyzers/metadata-analyzer.js'');
const { tinderParser } = require(''./dist/lib/parsers/tinder-parser.js'');
const fs = require(''fs'');
const { execSync } = require(''child_process'');

// Extract Tinder sample data
const zipOutput = execSync(''unzip -p examples/tinder-data.zip ""*/data.json""'', { encoding: ''utf-8'' });
const tinderData = JSON.parse(zipOutput);

// Parse the data
const parsed = tinderParser(tinderData);

// Run metadata analysis
const metadata = analyzeMetadata({
  messages: parsed.messages,
  matches: parsed.matches,
  participants: parsed.participants,
  userId: parsed.userId
}, ''Tinder'');

// Display results
console.log(''\n=== METADATA ANALYSIS RESULTS ===\n'');
console.log(''Platform:'', metadata.platform);
console.log(''\nSummary:'', metadata.summary);
console.log(''Assessment:'', metadata.assessment);
console.log(''\n--- Volume Metrics ---'');
console.log(''Total Matches:'', metadata.volume.totalMatches);
console.log(''Total Messages:'', metadata.volume.totalMessages);
console.log(''Active Conversations:'', metadata.volume.activeConversations);
console.log(''Avg Messages/Conversation:'', metadata.volume.averageMessagesPerConversation);
console.log(''Messages Sent:'', metadata.volume.messagesSentByUser);
console.log(''Messages Received:'', metadata.volume.messagesReceived);
console.log(''\n--- Timeline Metrics ---'');
console.log(''First Activity:'', metadata.timeline.firstActivity);
console.log(''Last Activity:'', metadata.timeline.lastActivity);
console.log(''Total Days Active:'', metadata.timeline.totalDays);
console.log(''Days Since Last Activity:'', metadata.timeline.daysSinceLastActivity);
console.log(''Peak Activity Period:'', metadata.timeline.peakActivityPeriod);
console.log(''\n--- Activity Distribution ---'');
console.log(''Matches by Month:'', metadata.distribution.matchesByMonth.slice(0, 5), ''...'');
console.log(''Messages by Month:'', metadata.distribution.messagesByMonth.slice(0, 5), ''...'');
")
